// Esto va a actuar como si fuera una base de local
// en verdad es un array de objetos

import { story } from "..";

let stories = [{
    id: 1,
    name: "Harry Potter",
    author: "J.K. Rowling",
    image: "https://phantom-marca.unidadeditorial.es/70cdeb1501a33c2c567dabbebc270ddd/resize/1320/f/jpg/assets/multimedia/imagenes/2021/11/17/16371568440299.jpg",
    description: "Harry Potter is a series of fantasy novels written by British author J. K. Rowling. The novels chronicle the lives of a young wizard, Harry Potter, and his friends Hermione Granger and Ron Weasley, all of whom are students at Hogwarts School of Witchcraft and Wizardry . The main story arc concerns Harry's struggle against Lord Voldemort, a dark wizard who intends to become immortal, overthrow the wizard governing body known as the Ministry of Magic, and subjugate all wizards and Muggles (non-magical people) worldwide.",
}, ];
/**
 * 
 Vamos a enviarle al cliente el siguiente
 en res podemos el statis res.status(200).json()
  {
    ok: // true || false esto va a indicar y si la peticion es success o error
    data: // Sera el cuerpo de nuestra respuesta esto va a tener un mensaje con los datos o un mensaje de error
  }
 */

// Listar
// METHOD: GET
export const list = (req, res) => {
    // lista los stories

    return res.status(200).json({
        ok: true,
        data: stories,
    });
};

// Crear 
// METHOD: POST
export const store = (req, res) => {
    // crea un story
    // note: Recurden que el estado de creacion es 201

    const body = req.body;
    body.id = stories.length + 1;
    stories.push(body);

    return res.status(201).json({
        ok: true,
        data: "Store Success",
    });
};

// Editar
// METHOD: PUT
export const update = (req, res) => {
    // edita un story

    /* let { story_id } = req.params;
    let { name } = req.body;
    let { author } = req.body;
    let { image } = req.body;
    let { description } = req.body;
    const change = stories.map((story) => story.story_id === story_id ? {
        ...story,
        name: change.name = name,
        ...story,
        author: change.author = author,
        ...story,
        image: change.image = image,
        ...story,
        description: change.description = description,
    } : story); */
    let id_story = +req.params.id;
    let body_story = req.body;
    let indice = stories.findIndex((story) => story.id === id_story);
    let change = { id: id_story, ...body_story };
    stories[indice] = change;

    return res.status(200).json({
        ok: true,
        data: "Story actualizada",
        change,
    });

};

// Eliminar
// METHOD: DELETE
export const destroy = (req, res) => {
    // elimina un story
    const { id } = req.params;
    stories = stories.filter((story) => story.id != id);
    return res.status(201).json({
        ok: true,
        data: "Story eliminada",
        stories,
    });
};
